const Post = ({
    title, date, explanation, copyright, url, mediaType
}) => {
    if (mediaType == "image"){
        return(
            <div>
                <img  className = "image" src = {url} width = "800px" height = "auto"></img>
                <Button id className = "likeButton"><i class="fas fa-heart"></i></Button>
                <p className = "copyright"> {copyright}</p>
                <h className = "title">{title} - {date}</h>
                <p className = "explanation"> {explanation}</p>

                <script>

                </script>

            </div>
        );
    }
    else{
        return(
            <div>
                {/* <img  className = "image" src = {url} width = "500px" height = "auto"></img> */}
                <iframe className = "image" width = "500px" height = "auto" src={url}>
                </iframe >
                <p className = "copyright"> {copyright}</p>
                <h className = "title">{title} - {date}</h>
                <p className = "explanation"> {explanation}</p>
            </div>
        );
    }

}

export default Post;
