import React, {Col, Row, Button} from  "react-bootstrap";
const Post = ({
    title, date, explanation, copyright, url, mediaType
}) => {
    const changeColor = () => {
        const btnElement = document.getElementById("btn");
        if (btnElement.style.color == "white"){
            btnElement.style.color = "red"
        }else{
            btnElement.style.color == "white"
        };

    }

    if (mediaType == "image"){
        return(
            <div>
                <img  className = "image" src = {url} width = "800px" height = "auto"></img>
                <Button onclick = {changeColor()} id = "btn" className = "likeButton"><i class="fas fa-heart"></i></Button>
                <p className = "copyright"> {copyright}</p>
                <h className = "title">{title} - {date}</h>
                <p className = "explanation"> {explanation}</p>

                {/* <script>
                    var btnElement = document.getElementById("btn");
                    function changeColor(){
                        if (btnElement.style.color == "white"){
                            btnElement.style.color == "red"
                        }else{
                            btnElement.style.color == "white"
                        }

                    }
                </script> */}
            </div>

        );
    }
    else{
        return(
            <div>
                {/* <img  className = "image" src = {url} width = "500px" height = "auto"></img> */}
                <iframe className = "image" width = "500px" height = "auto" src={url}>
                </iframe >
                <p className = "copyright"> {copyright}</p>
                <h className = "title">{title} - {date}</h>
                <p className = "explanation"> {explanation}</p>
            </div>
        );
    }

}

export default Post;
